package com.mycompany.study.ch02;

public class ExChar {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		char c1 = 'A';			// 문자
		char c2 = 65;			// 10진수
		char c3 = '\u0041';		// 16진수
		
		System.out.println ("c1: " + c1);
		System.out.println ("c2: " + c2);
		System.out.println ("c3: " + c3);
	}

}
